//get the input from the UI
//	the input is validated and accepts only numbers
//	if the input is NaN, then output an error message and return undefined, to flag the error to the caller
//if the input is valid, then disable the input control and return the sanitized input to the caller
//	so they can continue the lesson, given the input from the user

function pre_lesson_check(UI) {
	var validate = require('lib/lesson/util/array_lesson_argument_validate.js');
	var result = validate._.UI(UI);
	
	if (!result.valid) {
		throw new TypeError(result.msg);
	}
	
	//get user input to use during the lesson
	var input_tmp = UI.input.get();
	
	//if user input is NaN, then unusable, print error and return
	//NOTE: Number.isNaN() returns true on undefined
	if (Number.isNaN(input_tmp)) {
		UI.output.set(
			"The input is not a number.\n" +
			"Please enter a number and press\n" +
			"the 'Next' button to begin the\n" +
			"lesson.\n");
		return;
	}
	
	//after we've got the input, then disable the text input, until we're ready for another lesson
	UI.input.disable();
	
	return input_tmp;
}

module.exports = pre_lesson_check;