	//insert a value into the input
	ui.input.set("2");
	
	//EXEC
	var l = lesson({UI: ui});
	l._state = l._READY;	//setup correct state
	l._allocated_array = true;
	var result = l.pre_start();
	
	//TEST
	t.equal(l._state, l._DONE, 'lesson is finished');
