test('[lesson.next] correct, press Next button when not in RUN state', function (t) {
	//SETUP
	var ui = SETUP().real();
	
	var l = lesson({UI: ui});
	
	l._state = undefined;
	
	//TEST
	l.next();
	var result = ui.output.get();
	
	t.ok(result.match(/Enter a number above/));
	t.ok(result.match(/press 'Insert'/));
	
	TEARDOWN();
	t.end();
});

test('[lesson.pre_start] correct, press Insert button when not in READY state', function (t) {
	//SETUP
	var ui = SETUP().real();
	
	var l = lesson({UI: ui});
	
	l._state = undefined;
	
	//TEST
	l.pre_start();
	var result = ui.output.get();
	
	t.ok(result.match(/Press the 'Next' button/));
	t.ok(result.match(/continue the lesson/));
	
	TEARDOWN();
	t.end();
});