test('[array_setup -> x/y] correct', function (t) {
	//TEST
	var result = array_setup({size:1, x:0});
	
	t.equal(result.x, 0);
	t.equal(result.y, 150);
	
	result = array_setup({size:1, y:0});
	
	t.equal(result.x, 100);
	t.equal(result.y, 0);
	
	result = array_setup({size:1, x:0, y:0});
	
	t.equal(result.y, 0);
	t.equal(result.x, 0);
	
	result = array_setup({size:1});
	
	t.equal(result.x, 100);
	t.equal(result.y, 150);
	
	TEARDOWN();
	t.end();
});

