test('[array_setup -> ordered] correct several items', function (t) {
	//TEST
	var ui = SETUP();
	
	var array = array_setup({size:5, fill:5, ordered:true});
	
	ui.stage.add(array);
	
	t.plan(4);	//4 comparisons between each pair of numbers in 5 slots
	var last_value;
	for (var i = 1; i < array._.values.length; i++) {
		t.ok(array._.values[i-1] <= array._.values[i]);
	}
	
	TEARDOWN();
});

//NOTE: while unlikely, it is possible that the returned values in the array WILL BE IN SORTED ORDER, though chosen randomly
//	this test may fail on rare occasions
test('[array_setup -> ordered] correct, false', function (t) {
	//TEST
	var ui = SETUP();
	
	var array = array_setup({size:10, fill:10, ordered:false});
	
	var unsorted_values = array._.values.slice();
	var sorted_values = array._.values.slice();
	sorted_values.sort(function(a, b) {
		return a - b;
	});
	t.plan(1);
	//the two arrays should not be equal, as the random is likely to be unsorted in at least one position
	t.notEqual(unsorted_values, sorted_values);
	
	TEARDOWN();
});

