var move = require('lib/util/move');
var random_integer = require('lib/util/random_integer');

function test(UI) {
	console.log(UI);
	
	//make a button used for testing
	var button = new createjs.Shape();
	button.graphics.beginFill("red");
	button.graphics.beginStroke("black");
	button.graphics.drawRect(0, 0, 36, 18);
	
	//add the button to the stage
	UI._array_screen.addChild(button);
	UI._stage.update();
	
	//get a reference to the array members so we can traverse them
	var elements = UI._array.children;
	
	var idx = 0;
	
	//setup an event handler on the button
	var result = button.addEventListener("click", function(event) {
		var args = {source:elements[idx], destination:{y:100}, createjs:createjs}
		console.log(args);
		console.log(idx);
		if (move(args)) {
			if (idx < (elements.length-1)) idx += 1;
		}
	});
}

module.exports = test;